create database if not exists guncannon 
default character set utf8
default collate utf8_unicode_ci;

create table if not exists purchase_items(id int primary key auto_increment, name varchar(255), 
category varchar(255), specification varchar(255), unit varchar(255), price double, vendor varchar(255), 
phone varchar(255), origin varchar(255), purchaser varchar(255), purchasingDate timestamp, amount double);








